<script>

import $ from 'jquery'
import http from 'utils/http'
import BScroll from 'better-scroll'
export default {
    data(){
        return {
            //三个点的开关
            bStop:true,
            startX:'',
            startY:'',
            direction:'',
            content:''
        }
    },
    async beforeCreate(){
        let result = await http({
            method:'get',
            url:'/review.php?action=get_review_by_page&pid=24198400&main_pid=&product_medium=0&page=1&filtertype=2&label_id=&sid=ba0dff9c5d016c4382f1d4d1a71c4d9c'
        })
        this.content = result.data.review_list
    },
    mounted(){
        new BScroll('.detail-review',{
            probeType:1,
            click:true
        })
    },
    methods:{
        goback(){
            this.$router.go(-1)
            this.bStop1 = true
        },
        switch1(){
            this.bStop = !this.bStop
            if(!this.bStop){
                $('#list-list').animate({
                    marginTop:'0.44rem'
                })
            }else{
                $('#list-list').animate({
                    marginTop:'0'
                })
            }
        },
        getImg(e){
            let img = $(e.srcElement).attr('data-src')
            this.$store.commit('products/getImg',img)
        },

        handleTouchStart(e){
            this.startX = e.changedTouches[0].pageX;
            this.startY = e.changedTouches[0].pageY;
        },
        handleTouchMove(e){
            //获取滑动屏幕时的X,Y
            let endX = e.changedTouches[0].pageX;
            let endY = e.changedTouches[0].pageY;
            //获取滑动距离
            let distanceX = endX-this.startX;
            let distanceY = endY-this.startY;
            //判断滑动方向
            if(Math.abs(distanceX)>Math.abs(distanceY) && distanceX>0){
                this.direction = 1
            }else if(Math.abs(distanceX)>Math.abs(distanceY) && distanceX<0){
                this.direction = 0
            }
        
        },
        handleTouchEnd(e){
           if(this.direction == 0){
               this.$router.push('/detail/recommend')
           }else if(this.direction == 1){
               this.$router.push('/detail/review')
           }
        }
    }
}
</script>

